import React from "react";

const Food = ({title, totalTime, ingredients, image}) => {
    return(
        <div>
            <h1>{title}</h1>
            <p><b>Total Time: </b>{totalTime}</p>
            <o1><b><u>Ingredients</u></b>
                {ingredients.map(ingredient => (
                    <li>{ingredient.text}</li>
                ))}
            </o1>
            <img src={image} alt=""/>
        </div>
    );
}

export default Food;
