import React, { Component } from 'react';
import './App.css';

import Form from "./form";

const API_KEY="97ca8d43e4a249c08d36675136e074c0";

class App extends Component {
    state = {
        recipes: []
    }

    getRecipe = async (e) => {
        const recipeName = e.target.elements.recipeName.value;
        e.preventDefault();
        const api_call = await fetch (`"https://api.spoonacular.com/recipes/search?apiKey=db254b5cd61744d39a2deebd9c361444&number=1&query="`+q); 

        const results = await api_call.json();
        this.setState({ recipes: results.recipes})
        console.log(this.state.recipes)
    }
    render() {
        return (
            <div className = "App">
                <header className = "App-header">
                    <h1 className = "App-title">Recipe Search</h1>
                </header>
                <Form getRecipe={this.getRecipe}/>
                { this.state.recipes.map((recipe) => {
                    return <p>{ recipe.title }</p>
                }) }
            </div>
        )
    }
}
export default App
