import React, { Component } from 'react';
import './App.css';

import Form from "./form";

const API_ID="1d2d9dca"
const API_KEY="af7a6021e072d1ac2b7463b132cf1efa";
const exampleReq = `https://api.edamam.com/search?q=chicken&app_id=${API_ID}&app_key=${API_KEY}`

class App extends Component {
    state = {
        recipes: []
    }

    getRecipe = async (e) => {
        const recipeName = e.target.elements.recipeName.value;
        e.preventDefault();
        const api_call = await fetch (`"https://api.spoonacular.com/recipes/search?apiKey=db254b5cd61744d39a2deebd9c361444&number=1&query="`); 

        const results = await api_call.json();
        this.setState({ recipes: results.recipes})
        console.log(this.state.recipes)
    }
    render() {
        return (
            <div className = "App">
                <header className = "App-header">
                    <h1 className = "App-title">Recipe Search</h1>
                </header>
                <Form getRecipe={this.getRecipe}/>
                { this.state.recipes.map((recipe) => {
                    return <p>{ recipe.title }</p>
                }) }
            </div>
        )
    }
}
export default App
